import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const BottomNavigation = () => {
  const location = useLocation();
  
  const isActive = (path) => {
    if (path === '/' && location.pathname === '/') return true;
    if (path === '/players' && location.pathname === '/players') return true;
    if (path === '/tournaments' && (location.pathname === '/' || location.pathname.includes('/tournament/'))) return true;
    return false;
  };

  return (
    <nav className="bottom-navigation">
      <Link 
        to="/" 
        className={`nav-item ${isActive('/tournaments') ? 'active' : ''}`}
      >
        <div className="nav-icon">üèÜ</div>
        <span className="nav-label">Tournois</span>
      </Link>
      
      <Link 
        to="/players" 
        className={`nav-item ${isActive('/players') ? 'active' : ''}`}
      >
        <div className="nav-icon">üë•</div>
        <span className="nav-label">Joueurs</span>
      </Link>
      
      {/* Espace pour le FAB au centre */}
      <div className="nav-item fab-space">
        <div className="nav-icon"></div>
        <span className="nav-label"></span>
      </div>
      
      {location.pathname.includes('/tournament/') && (
        <>
          <Link 
            to={`${location.pathname.split('/tournament/')[0]}/tournament/${location.pathname.split('/tournament/')[1].split('/')[0]}/players`}
            className="nav-item"
          >
            <div className="nav-icon">üìù</div>
            <span className="nav-label">Participants</span>
          </Link>
          
          <Link 
            to={`${location.pathname.split('/tournament/')[0]}/tournament/${location.pathname.split('/tournament/')[1].split('/')[0]}/standings`}
            className="nav-item"
          >
            <div className="nav-icon">üìä</div>
            <span className="nav-label">Classement</span>
          </Link>
        </>
      )}
      
      {!location.pathname.includes('/tournament/') && (
        <>
          <div className="nav-item disabled">
            <div className="nav-icon">üìà</div>
            <span className="nav-label">Stats</span>
          </div>
          
          <div className="nav-item disabled">
            <div className="nav-icon">‚öôÔ∏è</div>
            <span className="nav-label">R√©glages</span>
          </div>
        </>
      )}
    </nav>
  );
};

export default BottomNavigation;
