import React, { useState } from 'react';

const FloatingActionButton = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [showImportModal, setShowImportModal] = useState(false);
  const [showExportModal, setShowExportModal] = useState(false);

  const handleImport = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const data = JSON.parse(e.target.result);
          // Ici vous pouvez ajouter la logique d'import
          console.log('Donn√©es import√©es:', data);
          setShowImportModal(false);
          setIsOpen(false);
        } catch (error) {
          alert('Erreur lors de l\'import: Format de fichier invalide');
        }
      };
      reader.readAsText(file);
    }
  };

  const handleExport = () => {
    // Ici vous pouvez r√©cup√©rer toutes les donn√©es depuis votre store/database
    const data = {
      tournaments: JSON.parse(localStorage.getItem('tournaments') || '[]'),
      players: JSON.parse(localStorage.getItem('players') || '[]'),
      matches: JSON.parse(localStorage.getItem('matches') || '[]'),
      exportDate: new Date().toISOString()
    };
    
    const dataStr = JSON.stringify(data, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    
    const link = document.createElement('a');
    link.href = url;
    link.download = `fab-tournament-export-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
    
    setShowExportModal(false);
    setIsOpen(false);
  };

  return (
    <>
      <div className={`fab-container ${isOpen ? 'open' : ''}`}>
        {/* Actions rapides */}
        <div className="fab-actions">
          <button 
            className="fab-action import"
            onClick={() => setShowImportModal(true)}
            title="Importer des donn√©es"
          >
            <span className="fab-icon">üìÅ</span>
            <span className="fab-text">Import</span>
          </button>
          
          <button 
            className="fab-action export"
            onClick={() => setShowExportModal(true)}
            title="Exporter des donn√©es"
          >
            <span className="fab-icon">üíæ</span>
            <span className="fab-text">Export</span>
          </button>
        </div>
        
        {/* Bouton principal */}
        <button 
          className={`fab-main ${isOpen ? 'open' : ''}`}
          onClick={() => setIsOpen(!isOpen)}
          title={isOpen ? 'Fermer' : 'Actions rapides'}
        >
          <span className="fab-icon">
            {isOpen ? '‚úï' : '+'}
          </span>
        </button>
      </div>

      {/* Modal d'import */}
      {showImportModal && (
        <div className="modal-overlay" onClick={() => setShowImportModal(false)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h3>üìÅ Importer des donn√©es</h3>
              <button 
                className="modal-close"
                onClick={() => setShowImportModal(false)}
              >
                ‚úï
              </button>
            </div>
            <div className="modal-body">
              <p>S√©lectionnez un fichier JSON contenant vos donn√©es de tournoi :</p>
              <input
                type="file"
                accept=".json"
                onChange={handleImport}
                className="file-input"
              />
              <div className="modal-info">
                <small>
                  ‚ÑπÔ∏è Le fichier doit √™tre au format JSON export√© par cette application.
                </small>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal d'export */}
      {showExportModal && (
        <div className="modal-overlay" onClick={() => setShowExportModal(false)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h3>üíæ Exporter des donn√©es</h3>
              <button 
                className="modal-close"
                onClick={() => setShowExportModal(false)}
              >
                ‚úï
              </button>
            </div>
            <div className="modal-body">
              <p>Voulez-vous exporter toutes vos donn√©es de tournoi ?</p>
              <div className="modal-actions">
                <button 
                  className="btn btn-primary"
                  onClick={handleExport}
                >
                  üíæ T√©l√©charger
                </button>
                <button 
                  className="btn btn-secondary"
                  onClick={() => setShowExportModal(false)}
                >
                  Annuler
                </button>
              </div>
              <div className="modal-info">
                <small>
                  ‚ÑπÔ∏è Toutes vos donn√©es (tournois, joueurs, matchs) seront export√©es dans un fichier JSON.
                </small>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default FloatingActionButton;
