import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { PlayerService } from '../services/database';

const GlobalPlayerManagement = () => {
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingPlayer, setEditingPlayer] = useState(null);
  const [formData, setFormData] = useState({
    name: ''
  });

  useEffect(() => {
    const loadPlayers = async () => {
      try {
        setLoading(true);
        const playersData = await PlayerService.getAllPlayers();
        setPlayers(playersData);
      } catch (err) {
        setError('Erreur lors du chargement: ' + err.message);
      } finally {
        setLoading(false);
      }
    };
    
    loadPlayers();
  }, []);

  const reloadPlayers = async () => {
    try {
      const playersData = await PlayerService.getAllPlayers();
      setPlayers(playersData);
    } catch (err) {
      setError('Erreur lors du rechargement: ' + err.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.name.trim()) return;

    try {
      if (editingPlayer) {
        await PlayerService.updatePlayer(editingPlayer.id, formData);
      } else {
        await PlayerService.createPlayer(formData);
      }
      
      resetForm();
      await reloadPlayers();
    } catch (err) {
      setError('Erreur lors de la sauvegarde: ' + err.message);
    }
  };

  const handleEdit = (player) => {
    setEditingPlayer(player);
    setFormData({
      name: player.name
    });
    setShowAddForm(true);
  };

  const handleDelete = async (playerId) => {
    if (!window.confirm('√ätes-vous s√ªr de vouloir supprimer ce joueur ? Cette action est irr√©versible.')) {
      return;
    }

    try {
      await PlayerService.deletePlayer(playerId);
      await reloadPlayers();
    } catch (err) {
      setError('Erreur lors de la suppression: ' + err.message);
    }
  };

  const handleToggleActive = async (player) => {
    try {
      await PlayerService.updatePlayer(player.id, {
        isActive: !player.isActive
      });
      await reloadPlayers();
    } catch (err) {
      setError('Erreur lors de la mise √† jour: ' + err.message);
    }
  };

  const resetForm = () => {
    setFormData({ name: '' });
    setEditingPlayer(null);
    setShowAddForm(false);
  };

  if (loading) {
    return <div className="loading">Chargement des joueurs...</div>;
  }

  return (
    <div>
      <div className="flex-between mb-3">
        <div>
          <h2>Gestion des Joueurs</h2>
          <p className="text-secondary">G√©rez votre base de donn√©es de joueurs FAB</p>
        </div>
        <div className="flex gap-2">
          <button 
            className="btn btn-primary"
            onClick={() => setShowAddForm(true)}
          >
            ‚ú® Nouveau Joueur
          </button>
          <Link to="/" className="btn btn-secondary">
            üè† Accueil
          </Link>
        </div>
      </div>

      {error && (
        <div className="error">
          {error}
          <button 
            className="btn btn-small" 
            onClick={() => setError(null)}
            style={{ marginLeft: '1rem' }}
          >
            ‚úï
          </button>
        </div>
      )}

      {showAddForm && (
        <div className="card mb-3">
          <div className="card-header">
            <h3 className="card-title">
              {editingPlayer ? '‚úèÔ∏è Modifier le joueur' : '‚ûï Ajouter un joueur'}
            </h3>
